    private void backLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_backLoginActionPerformed
        Container.removeAll();
        Container.repaint();
        Container.revalidate();
        
        Container.add(Login);
        Container.repaint();
        Container.revalidate();
    }//GEN-LAST:event_backLoginActionPerformed

