    private void registerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_registerActionPerformed
        Container.removeAll();
        Container.repaint();
        Container.revalidate();
        
        Container.add(Register);
        Container.repaint();
        Container.revalidate();
        
    }//GEN-LAST:event_registerActionPerformed

